import React, { useState } from 'react'
import { useLocation } from 'react-router';

const Navbar = () => {
    // 获取当前路由的信息
    const location = useLocation();
    const flag = location.pathname === '/official'

    const [tag, setTag] = useState('社会招聘')

    const menuItem = [
        { name: '社会招聘', href: '/official/social/' },
        { name: '实习招聘', href: '/official/trainee/' },
        { name: '校园招聘', href: 'https://campus.kuaishou.cn/recruit/campus/e/#/campus/index/' },
        { name: '关于快手', href: '/official/jianghu/' },
    ];

    const handleActive = (itemName) => {
        setTag(itemName)
    }   

    return (
        <div className='k-home-header' style={flag ? { background: 'transparent' } : { background: '#ffffff' }}>
            <a className="k-home-header-logo" href="/official">
                <div className="k-home-header-logo-img" style={flag ? { backgroundImage: 'url(../img/logo.png)' } : { backgroundImage: 'url(../img/logo.white.png)' }} >
                </div>
            </a >
            <ul>
                {menuItem.map(item => (
                    <li className={`k-home-header-menu-item ${tag === item.name && !flag ? 'active' : ''}`} onClick={() => handleActive(item.name)}>
                        <a href={item.href} className={flag ? 'activeA' : 'activeNo'}>{item.name}</a>
                    </li>
                ))}
            </ul>
            <div className="k-home-header-dropdown">
                <i className="common-presenter k-common-avatar"></i>
                <a href="#/official/login/">注册/登录</a>
            </div>
        </div >
    )
}

export default Navbar
