import React, { useState } from 'react';
import FitersItem from './FitersItem';
import Texttips from './Texttips';

const SocialIndexPage = () => {
  const locations1 = ['国内', '北京', '上海', '广州', '深圳', '天津', '杭州', '成都', '武汉', '济南', '青岛', '烟台', '沈阳', '哈尔滨', '长春', '石家庄', '无锡', '淮安', '铜仁', '吉首', '苏州', '澄迈', '郑州', '重庆', '东莞', '唐山', '临沂', '保定']
  const location2 = ['国外', '圣保罗', '雅加达']
  const jobCategories = [
    '全部', '工程类', '算法类',
    '产品类', '运营类', '设计类',
    '分析类', '战略类', '市场类',
    '职能类', '客服类', '审核类',
    '内容评级类', '销售及支持类', '其它类'
  ];
  const texttips = ['不限','产品经理','效果设计','推荐算法','音视频','Android']
  const n = texttips.length - 1


  // 过滤掉"全部"，然后用逗号连接成一个字符串
  const filteredJobs = jobCategories.filter(category => category !== '全部').join(', ');
  const [active, setActive] = useState(0)

  // 点击texttips切换样式


  // 根据选定的地点和类别来过滤职位信息。
  return (
    <div className="k-social-index-page">
      <div className="social social-index-banner"></div>
      <div className="social-index-filters">
        <div className="k-common-filters">
          <div className="k-common-filters-title">工作地点</div>
          <div className="k-common-filters-items-row">
            <FitersItem data={locations1} />
            <FitersItem data={location2} />
          </div>
        </div>
        <div className="k-common-filters">
          <div className="k-common-filters-title">职位类别</div>
          <FitersItem data={jobCategories} />
        </div>
      </div>
      <div className="social-index-search">
        <div className="social-index-search">
          <span className='search-hot-title'>热门搜索</span>
          <div class="k-common-text-tips">
          {texttips.map((tip,index) => (
             <div class="k-common-text-tips-item" onClick={handleTips}>{tip}</div> 
             && index !== n ?
             <i class="k-common-text-tip-i">|</i> : ''
          ))}
        </div>

          {/* 搜索框 */}
          <div className="k-common-search">
            <div className="ant-input-search">
              <input type="text" placeholder='搜索职位'/>
              <span className='iconfont'>&#xe6cf;</span>
            </div>
          </div>
        </div>
        <div className="k-common-search">

        </div>
      </div>
    </div>
  );
};

export default SocialIndexPage;
