import React, { useState } from 'react'
import { useLocation } from 'react-router';

const Navbar = () => {
    const [flag, setFlag] = useState(true)
    const [tag, setTag] = useState('社会招聘')


    const menuItem = [
        { name: '社会招聘', href: '/official/social/' },
        { name: '实习招聘', href: '/official/trainee/' },
        { name: '校园招聘', href: 'https://campus.kuaishou.cn/recruit/campus/e/#/campus/index/' },
        { name: '关于快手', href: '/official/jianghu/' },
    ];

    const handleActive = (itemName, itemHref) => {
        setTag(itemName);

        if (itemHref === '/official') {
            // 如果是社会招聘页面，则将 flag 设为 true，应用透明背景
            setFlag(true);
        } else {
            // 否则将 flag 设为 false，应用白色背景
            setFlag(false);
        }
    }



    return (
        <div className='k-home-header' style={flag ? { background: 'transparent' } : { background: '#ffffff' }}>
            <a className="k-home-header-logo" href="/official">
                <div className={`k-home-header-logo-img ${flag ? '' : 'logo'}`}  >
                </div>
            </a>
            <ul>
                {menuItem.map(item => (
                    <li className={`k-home-header-menu-item ${tag === item.name && !flag ? 'active' : ''}`} onClick={() => handleActive(item.name, item.href)}>
                        <a href={item.href} className={flag ? 'activeA' : 'activeNo'}>{item.name}</a>
                    </li>
                ))}
            </ul>
            <div className="k-home-header-dropdown">
                <i className="common-presenter k-common-avatar"></i>
                <a href="#/official/login/">注册/登录</a>
            </div>
        </div>
    )
}

export default Navbar
