import {
  createBrowserRouter,
  Outlet,
  RouterProvider,
  useLocation,
} from "react-router-dom";
import "./style.scss"
import Home from "./pages/Home";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import SocialIndexPage from "./components/SocialIndexPage";




const Layout = () => {
  // 获取当前路由的信息
  const location = useLocation();
  // 改导航栏的样式
  const navbarStyle = location.pathname === '/official' ? { background: 'transparent' } : { background: '#ffffff' };
  return (
    <>
      <Navbar style={navbarStyle} />
      <Outlet />
      <Footer />
    </>
  );
};

const router = createBrowserRouter([
  {
    path: "/",
    element: <Layout />,
    children: [
      {
        path: "/official",
        element: <Home />
      }
      , {
        path: "/official/social",
        element: <SocialIndexPage />
      }

    ]
  },
]);

function App() {

  return (
    <div className="app">
      <div className="container">
        <RouterProvider router={router} />
      </div>
    </div>
  )
}

export default App;
