import React, { useState } from 'react';

const SocialIndexPage = () => {
  const [selectedLocation, setSelectedLocation] = useState('国内');
  const [selectedCategory, setSelectedCategory] = useState('全部');

  const handleLocationChange = (location) => {
    setSelectedLocation(location);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  // 根据选定的地点和类别来过滤职位信息。

  return (
    <div className="k-social-index-page">
      <div className="social social-index-banner"></div>
      <div className="social-index-filters">
        <div className="k-common-filters">
          <div className="k-common-filters-title">工作地点</div>
          <div className="k-common-filters-items-row">
            <div className="k-common-filters-items">
              <div className="k-common-filters-items-tags">
                {/* 这里循环渲染工作地点标签 */}
                {['国内', '北京', '上海', '广州', '深圳', '天津', '杭州', '成都', '武汉', '济南', '青岛', '烟台', '沈阳', '哈尔滨', '长春', '石家庄', '无锡', '淮安', '铜仁', '吉首', '苏州', '澄迈', '郑州', '重庆', '东莞', '唐山', '临沂', '保定', '国外', '圣保罗', '雅加达'].map((location) => (
                  <div className="filters-item" key={location}>
                    <div
                      // 用户点击时按钮也发生相应的变化
                      className={`ant-tag ant-tag-checkable ${selectedLocation === location ? 'ant-tag-checkable-checked' : ''
                        }`}
                      onClick={() => handleLocationChange(location)}
                    >
                      {location}
                    </div>
                  </div>
                ))}
              </div>
              <div className="k-common-filters-items-expand">
                <i></i>
              </div>
            </div>


          </div>
        </div>

      </div>
      {/* 添加表格渲染职位信息的代码 */}
    </div>
  );
};

export default SocialIndexPage;
