import React, { useState } from 'react'

const Navbar = () => {
    const [tag, setTag] = useState('社会招聘')

    const menuItem = [
        { name: '社会招聘', href: '/official/trainee/' },
        { name: '实习招聘', href: '/official/trainee/' },
        { name: '校园招聘', href: 'https://campus.kuaishou.cn/recruit/campus/e/#/campus/index/' },
        { name: '关于快手', href: '/official/jianghu/' },
    ];

    const handleActive = (itemName) => {
        setTag(itemName)
    }

    return (
        <div className='k-home-header'>
            <a class="k-home-header-logo" href="#/official/index/">
                <div class="k-home-header-logo-img">
                </div>
            </a>
            <ul>
                {menuItem.map(item => (
                    <li className={`k-home-header-menu-item ${tag === item.name ? 'active' : ''}`} onClick={() => handleActive(item.name)}><a href={item.href}>{item.name}</a></li>
                ))}
            </ul>
            <div className="k-home-header-dropdown">
                <i class="common-presenter k-common-avatar"></i>
                <a href="#/official/login/">注册/登录</a>
            </div>
        </div>
    )
}

export default Navbar
